})

options.onLazyLoad:
const { level, value, childrenData } = node
if(!!childrenData) {
  resolve(childrenData)
  return
}
const idOrPath = level == 0 ? "/" : value
$api.post('/nuxeo/document/children/thumbnail', {idOrPath, pageSize: 200}).then(res => {
  const nodes = res.data.data.entryList.reduce((prev, item) => { 
    if(item.isFolder) prev.push({
      value: item.id,
      label: item.name
    })
    return prev
  },[])
  // 通过调用resolve将子节点数据返回，通知组件数据加载完成
  resolve(nodes)
}).catch(err => {
  resolve([])
})

options.onMounted:
init()
const _this = this
async function init () {
  const groupList = await getGroupList()
  const userList = await getUserList()
  const list = [
    { label: $i18n.t('user_groups'), options: groupList },
    { label: $i18n.t('user_users'), options: userList }
  ]
  _this.loadOptions(list)
  // 
}
async function getGroupList () {
  try {
    const data = await $api.post('/nuxeo/identity/groups', {}).then(res => res.data.data)
    const result = data.reduce((prev,item) => {
      prev.push({
        value: `group:${item.id}`,
        label: item.name
      })
      return prev
    }, [])
    return result.sort((a,b)=> (a.label.localeCompare(b.label) ))
  } catch (e) {
    return[]
  }
}
async function getUserList () {
  try {
    const data = await $api.post('/nuxeo/identity/users', {}).then(res => res.data.data)
    const result =  data.reduce((prev,item) => {
      if(item.username && item.userId) {
        prev.push({
          value: `user:${item.userId}`,
          label: item.username
        })
      }
      return prev
    }, [])
    return result.sort((a,b)=> (a.label.localeCompare(b.label) ))
  } catch (e) {
    return[]
  }
}

options.onChange:
const hideList = []
const disabledList = ['rootId']
if(value) {
  hideList.forEach(item => {
    const ref = this.getWidgetRef(item)
    if (!!ref) {
      ref.setHidden(true)
    }
  })
  disabledList.forEach(item => {
    const ref = this.getWidgetRef(item)
    if (!!ref) {
      ref.setDisabled(true)
    }
  })
}
