formConfig.onFormMounted:
const _this = this
async function getDocTypeList() {
  window.masterTableDataTypeList = await $api.get('/docpal/master/tables/dataType/mapping', {}).then(res => res.data.data)
  return window.masterTableDataTypeList
}
async function setMetaList() {
  const docTypeList = await getDocTypeList()
  const fieldsWidget = _this.getWidgetRef('fields')
  const rowIdData = fieldsWidget.rowIdData
  const refList = fieldsWidget.refList
  rowIdData.forEach(item => {
    refList[`dataType@row${item}`].loadOptions(docTypeList)
  })
}
setMetaList()
