options.onCreated:
const options = [
    { label: $t('searchType_Picture'), value: 'Picture' },
    { label: $t('searchType_Video'), value: 'Video' },
    { label: $t('searchType_Audio'), value: 'Audio' }
]
this.loadOptions(options)

options.onChange:
// get component of type
const docType = this.getWidgetRef('type');
const author = this.getWidgetRef('authors');
const modified = this.getWidgetRef('modified');
const includeFolder = this.getWidgetRef('includeFolder');
const collections = this.getWidgetRef('collections');
const tags = this.getWidgetRef('tags');
const size = this.getWidgetRef('size');

// picture or Video
const mimeType = this.getWidgetRef('mimeType');
const height = this.getWidgetRef('height');
const width = this.getWidgetRef('width');
const duration = this.getWidgetRef('duration');

const allEnableList = [docType, author, modified, includeFolder, collections, tags, size ];
const allHiddenList = [mimeType, height, width, duration];

const pictureEnableList = [author, mimeType, height, width, modified, size, collections,tags]
const pictureHiddenList = [docType, includeFolder, duration ]

const videoEnableList = [author, mimeType, height, width, duration, modified, size, collections,tags,]
const videoHiddenList = [docType, includeFolder, duration ]

const auidioEnableList = [author, mimeType, modified, size, collections,tags,]
const audioHiddenList = [docType, includeFolder, duration, height, width, ]
switch (value) {

  case 'Video':
     lootWidgetItem(videoHiddenList, true, true);
    lootWidgetItem(videoEnableList, false, false);
    break;
  case 'Picture':
    lootWidgetItem(pictureHiddenList, true, true);
    lootWidgetItem(pictureEnableList, false, false);
    break;
  case 'Audio':
    lootWidgetItem(audioHiddenList, true, true);
    lootWidgetItem(auidioEnableList, false, false);
    break;
  default :
    lootWidgetItem(allHiddenList, true, true);
    lootWidgetItem(allEnableList, false, false);
    break;
}
function lootWidgetItem(widgetList, isHidden, setNull) {
  widgetList.forEach(widgetItem => {
    if(widgetItem) {
      widgetItem.setHidden(isHidden)
      if(setNull) {
        widgetItem.setValue(null);
      }
    }
  })
}
if(value) {
  const response = $api.get(`/nuxeo/search/getSearchExtends?primaryType=${value}`);
  response.then(res => {
    if(res.data.data.mimeType) {
      const options = Object.keys(res.data.data.mimeType).map( item => ({label:item, value:item}));
      mimeType.loadOptions(options);
    }
    if(res.data.data.height) {
      const options = Object.keys(res.data.data.height).map( item => ({label:item, value:item}));
      height.loadOptions(options)
    }
    if(res.data.data.duration) {
      const options = Object.keys(res.data.data.duration).map( item => ({label:item, value:item}));
      duration.loadOptions(options)
    }
    if(res.data.data.width) {
      const options = Object.keys(res.data.data.width).map( item => ({label:item, value:item}));
      width.loadOptions(options)
    }

  })
}

options.onMounted:
$api.post('/nuxeo/types', {}).then(res => {
   res = res.data.data
  const reponse = res.map(item => ({
    value: item.name,
    label: $t(item.name)
  }))
  reponse.sort((a, b) => a.label.localeCompare(b.label))
  this.loadOptions(reponse)
}).catch(err => {
  console.log(err)
})

const _this = this
const creatorRef = _this.getWidgetRef('creator')
$api.post('/nuxeo/identity/getKeyCloakAllUsers', {}).then(res => {
  res = res.data.data
  const result = []
  res.forEach(item => {
    if(item.username || item.userId) {
      result.push({
        value: item.userId || item.username,
        label: item.username
      })
    }
  })
  result.sort((a, b) => a.label.localeCompare(b.label))
  _this.loadOptions(result)
  creatorRef.loadOptions(result)
}).catch(err => {
})

